mapper.insert:
INSERT INTO BOARD_IMG(BOARD_IMG_NO, ORIGIN_NAME, CHANGE_NAME, REF_BNO, IMG_LEVEL) SELECT SEQ_INO.NEXTVAL , C.* FROM (
SELECT #{boardImg.originName} , #{boardImg.changeName} , #{boardImg.refBno} , #{boardImg.imgLevel} FROM DUAL
) C

mapper.update:
UPDATE BOARD 
   SET COUNT = COUNT +1
 WHERE BOARD_NO = #{boardNo}

UPDATE BOARD 
SET BOARD_TITLE = #{boardTitle} ,
	BOARD_CONTENT = #{boardContent}

WHERE BOARD_NO = #{boardNo}

UPDATE BOARD_IMG SET
ORIGIN_NAME = #{originName} , 
CHANGE_NAME = #{changeName}
WHERE REF_BNO = #{refBno} AND IMG_LEVEL= #{imgLevel}
