mapper.insert:
INSERT INTO BOARD_IMG(BOARD_IMG_NO, ORIGIN_NAME, CHANGE_NAME, REF_BNO, IMG_LEVEL) SELECT SEQ_IMG_NO.NEXTVAL , C.* FROM (
SELECT #{bi.originName}, #{bi.changeName} , #{bi.refBno} , #{bi.imgLevel} FROM DUAL
) C

mapper.select:
SELECT * FROM BOARD_IMG
WHERE REF_BNO = #{boardNo}
ORDER BY IMG_LEVEL

mapper.update:
UPDATE BOARD SET
COUNT = COUNT +1
WHERE BOARD_NO = #{boardNo}
